import React from "react";
import EmbeddedLink from "./EmbeddedLink.js";
import { decompressToBase64 } from "../utils/slug";

export default function SingleReq({ idx, dtype, k, v }) {
  return (
    <div key={idx} className="flex flex-col gap-2">
      {/* Caption always shown */}
      <span className="text-sm text-gray-400">{k}</span>

      {dtype === "text" && (() => {
        const isHTML = typeof v === "string" && /<\/?[a-z][\s\S]*>/i.test(v);
        if (isHTML) {
          return (
            <div
              className="text-sm text-white break-words"
              dangerouslySetInnerHTML={{ __html: v }}
            />
          );
        } else {
          return <div className="text-sm text-white break-words">{v}</div>;
        }
      })()}



      {dtype === "link" && <EmbeddedLink k={k} v={v} />}

      {dtype === "image" && (
        <img
          src={decompressToBase64(v)}
          alt={k || ""}
          className="w-full h-auto max-h-[80vh] object-contain rounded"
        />
      )}

      {dtype === "audio" && (
        <audio controls className="w-full">
          <source src={decompressToBase64(v)} type="audio/mpeg" />
        </audio>
      )}

      {dtype === "video" && (
        <video
          controls
          className="w-full h-auto max-h-[80vh] object-contain rounded"
        >
          <source src={decompressToBase64(v)} type="video/mp4" />
        </video>
      )}




      {/*For imported HTML*/}
      <style>
      {`

        .html-content h1 {
          @apply text-purple-600 font-bold text-4xl;
        }
        .html-content h2 {
          @apply text-purple-600 font-bold text-3xl;
        }
        .html-content h3 {
          @apply text-purple-600 font-bold text-2xl;
        }
        .html-content h4 {
          @apply text-purple-600 font-bold text-xl;
        }
        .html-content h5 {
          @apply text-purple-600 font-bold text-lg;
        }
        .html-content h6 {
          @apply text-purple-600 font-bold text-base;
        }

      `}
      </style>


    </div>
  );
}
