import { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";

export default function Navbar({ addedClassList = "" }) {
  const navigate = useNavigate();
  const dropdownRef = useRef(null);

  const [user, setUser] = useState({});
  const [view, setView] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) setUser(JSON.parse(storedUser));
  }, []);

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setView(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div
      className={`flex flex-row items-center px-6 py-4 w-full bg-zinc-950 shadow-[0_0_20px_#a855f7aa] z-40 ${addedClassList}`}
    >
      <a href="/" className="text-yellow-300 hover:text-white transition duration-300">
        <i className="fa-solid fa-ranking-star text-3xl sm:text-4xl" />
      </a>

      <div className="ml-auto relative" ref={dropdownRef}>
        <button
          className="bg-black border border-purple-700 text-purple-400 font-semibold px-4 py-2 rounded hover:shadow-[0_0_10px_#a855f7aa] transition-all duration-300"
          onClick={() => setView(!view)}
        >
          {user.username}
        </button>

        <div
          className={`absolute right-0 mt-2 w-44 bg-zinc-900 rounded-lg shadow-lg overflow-hidden transition-all duration-300 transform origin-top ${
            view ? "opacity-100 scale-100" : "opacity-0 scale-95 pointer-events-none"
          }`}
        >
          <button
            className="w-full text-left px-4 py-2 text-white hover:bg-purple-700 transition"
            onClick={() => {
              localStorage.removeItem("user");
              navigate("/login");
            }}
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
}
