import Navbar from "../components/Navbar";
import EmbeddedLink from "../components/EmbeddedLink";
import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { decompressToBase64 } from "../utils/slug";



//For a single req
function Req({ entries, reqName, i, dtype, captions }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const current = entries[currentIndex];
  const currentCaption = captions[currentIndex];
  const k = currentCaption ? currentCaption : reqName;
  const v = current;

  const next = (e) => {
    e.stopPropagation();
    setCurrentIndex((prev) => (prev + 1) % entries.length);
  };

  const prev = (e) => {
    e.stopPropagation();
    setCurrentIndex((prev) => (prev - 1 + entries.length) % entries.length);
  };

  return (
    <div className="p-4 rounded-2xl bg-zinc-900 shadow-[0_0_10px_#a855f7aa] text-white" key={i}>
      <h2 className="text-lg font-semibold text-purple-400 mb-4">{reqName}</h2>

      <div className="mb-4">
        {dtype === "text" && (
          <div className="flex justify-between text-sm">
            <span className="text-gray-400">{k}</span>
            {/<[a-z][\s\S]*>/i.test(v) ? (
              <span className="text-white" dangerouslySetInnerHTML={{ __html: v }} />
            ) : (
              <span className="text-white">{v}</span>
            )}
          </div>
        )}

        {dtype === "link" && <EmbeddedLink k={k} v={v} />}

        {dtype === "image" && (
          <div className="flex flex-col items-center">
            <span className="text-sm text-gray-400 mb-1">{k}</span>
            <img
              src={decompressToBase64(v)}
              alt="submission image"
              className="max-w-full max-h-60 rounded shadow"
            />
          </div>
        )}

        {dtype === "audio" && (
          <div className="text-center">
            <p className="text-sm text-gray-400 mb-1">{k}</p>
            <audio controls className="w-full">
              <source src={decompressToBase64(v)} type="audio/mpeg" />
              Your browser does not support audio.
            </audio>
          </div>
        )}

        {dtype === "video" && (
          <div className="text-center">
            <p className="text-sm text-gray-400 mb-1">{k}</p>
            <video controls className="mx-auto max-w-full max-h-72 rounded shadow">
              <source src={decompressToBase64(v)} type="video/mp4" />
              Your browser does not support video.
            </video>
          </div>
        )}
      </div>

      <div className="flex justify-between text-sm text-gray-500">
        <button onClick={prev} className="hover:text-white">&larr; Prev</button>
        <span>{currentIndex + 1} / {entries.length}</span>
        <button onClick={next} className="hover:text-white">Next &rarr;</button>
      </div>
    </div>
  );
}




export default function ComparePage() {
  const navigate = useNavigate();
  const api_link = "http://localhost:2022/api";
  const { id } = useParams();

  const [ld_name, setLdName] = useState(undefined);
  const [ld, setLd] = useState(undefined);
  const [choices, setChoices] = useState({});
  const [previousPicks, setPreviousPicks] = useState([]);

  async function submitAndPoll(pick) {
    if (pick !== "" && choices.choice1 && choices.choice2) {
      const winner = choices["choice" + pick]._id;
      const loser = choices[pick === "1" ? "choice2" : "choice1"]._id;
      const similarity = choices.score;
      setPreviousPicks([...previousPicks, choices["choice" + pick].name, choices["choice" + (pick === "1" ? "1" : "2")].name]);
      await axios.post(api_link + "/rank/rank", { winner, loser, similarity });
    }

    setChoices({});
    await new Promise((resolve) => setTimeout(resolve, 100));

    if (ld_name !== undefined) {
      const { data } = await axios.post(api_link + "/rank/poll", {
        leaderboard: ld_name,
        previousPicks,
      });
      setChoices(data);
    }
  }

  async function updateLd() {
    const { data } = await axios.post(api_link + "/leaderboard/getLeaderboardById", { id });
    const ldb = data.leaderboard;
    setLd(ldb);
    setLdName(ldb.name);
    submitAndPoll("");
  }

  useEffect(() => {
    updateLd();
  }, []);

  useEffect(() => {
    submitAndPoll();
  }, [ld_name]);

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-zinc-900 to-black text-white">
      <Navbar />

      <div className="max-w-6xl mx-auto px-4 py-10">
        <h1 className="text-4xl font-bold mb-8 text-center">
          Playing <span className="text-purple-400">{ld_name}</span>
        </h1>

        {choices.choice1 && choices.choice2 && ld && (
          <div className="flex flex-col md:flex-row gap-6 justify-between">
            {[choices.choice1, choices.choice2].map((choice, index) => (
              <div
                key={index}
                className="flex-1 bg-zinc-950 hover:bg-zinc-900 rounded-2xl p-6 shadow-[0_0_25px_#a855f7aa] cursor-pointer transition-transform hover:scale-[1.02]"
                onClick={() => submitAndPoll(index === 0 ? "1" : "2")}
              >
                <h2 className="text-2xl font-semibold mb-2 text-purple-400">{choice.name}</h2>
                <p className="text-sm text-gray-300 mb-4">ELO: {Math.round(choice.elo)}</p>

                <div className="space-y-3">
                  {ld.required.map((req, i) => {
                    const name = req.name;
                    const keys = Object.keys(choice.data).filter((b) => {
                      const escapeRegex = (str) => str.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
                      const regex = new RegExp(`^${escapeRegex(name)} \\d+$`);
                      return regex.test(b);
                    });
                    const entries = keys.map((x) => choice.data[x]);
                    const caption_keys = keys.map((x) => `${x} caption`);
                    const captions = caption_keys.map((x) => choice.data[x]);
                    return (
                      <Req
                        entries={entries}
                        reqName={name}
                        i={i}
                        dtype={req.type}
                        captions={captions}
                      />
                    );
                  })}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
