import React from "react";

//For if we're given a link but we wanna consider embedding it into the webpage

export default function EmbeddedLink({ k, v }) {
  const url = v?.toLowerCase() || "";

  // Extract YouTube video ID
  const getYouTubeId = (link) => {
    try {
      if (link.includes("youtu.be")) {
        return link.split("/").pop().split("?")[0];
      }
      const urlObj = new URL(link);
      return urlObj.searchParams.get("v");
    } catch {
      return null;
    }
  };

  // Extract Vimeo video ID
  const getVimeoId = (link) => {
    try {
      return link.split("/").filter(Boolean).pop();
    } catch {
      return null;
    }
  };

  // RENDER IMAGE
  if (url.match(/\.(jpeg|jpg|gif|png|webp|svg)$/i) || url.startsWith("data:image")) {
    return <img src={v} alt={k} className="max-w-full max-h-64 rounded shadow" />;
  }

  // EMBEDDED YOUTUBE VIDEO
  const ytId = getYouTubeId(v);
  if (ytId) {
    return (
      <iframe
        className="w-full max-w-xl aspect-video rounded shadow"
        src={`https://www.youtube.com/embed/${ytId}`}
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
        title={k}
      />
    );
  }

  // EMBEDDED VIMEO VIDEO
  if (url.includes("vimeo.com")) {
    const vimeoId = getVimeoId(v);
    return (
      <iframe
        src={`https://player.vimeo.com/video/${vimeoId}`}
        className="w-full max-w-xl aspect-video rounded shadow"
        frameBorder="0"
        allow="autoplay; fullscreen; picture-in-picture"
        allowFullScreen
        title={k}
      />
    );
  }

  // RENDER VIDEO
  if (url.match(/\.(mp4|webm|ogg)$/i)) {
    return (
      <video controls className="max-w-full max-h-64 rounded shadow">
        <source src={v} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    );
  }

  // RENDER AUDIO
  if (url.match(/\.(mp3|wav|ogg)$/i)) {
    return (
      <audio controls className="w-full mt-2">
        <source src={v} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
    );
  }

  // FALLBACK: LINK
  return (
    <a
      className="block text-purple-400 underline text-sm"
      href={v}
      target="_blank"
      rel="noopener noreferrer"
    >
      {k}
    </a>
  );
}
