import React, { useState } from "react";
import EmbeddedLink from "./EmbeddedLink.js";
import { decompressToBase64 } from "../utils/slug";
import "../styles/htmlContent.css";

export default function SingleReq({ idx, dtype, k, v }) {
  const [expanded, setExpanded] = useState(false); // only for text

  const isHTML = dtype === "text" && typeof v === "string" && /<\/?[a-z][\s\S]*>/i.test(v);

  return (
    <div className="flex flex-col gap-2">
      {/* Caption */}
      <span className="text-sm text-gray-400">{k}</span>

      {/* Text */}
      {dtype === "text" && (
        <div>
          <div
            className={`text-sm text-white break-words overflow-hidden transition-all duration-300 ${
              expanded ? "max-h-[20rem]" : "max-h-[1.5rem]"
            } html-content`}
            {...(isHTML ? { dangerouslySetInnerHTML: { __html: v } } : {})}
          >
            {!isHTML && v}
          </div>

          <button
            onClick={() => setExpanded(!expanded)}
            className="mt-2 text-purple-400 hover:text-purple-300 text-sm font-medium"
          >
            {expanded ? "Show less" : "Show more"}
          </button>
        </div>
      )}

      {/* Link */}
      {dtype === "link" && <EmbeddedLink k={k} v={v} />}

      {/* Image */}
      {dtype === "image" && (
        <img
          src={decompressToBase64(v)}
          alt={k || ""}
          className="w-full h-auto max-h-[80vh] object-contain rounded"
        />
      )}

      {/* Audio */}
      {dtype === "audio" && (
        <audio controls className="w-full">
          <source src={decompressToBase64(v)} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
      )}

      {/* Video */}
      {dtype === "video" && (
        <video
          controls
          className="w-full h-auto max-h-[80vh] object-contain rounded"
        >
          <source src={decompressToBase64(v)} type="video/mp4" />
          Your browser does not support the video element.
        </video>
      )}
    </div>
  );
}
