import Navbar from "../components/Navbar";
import Req from "../components/Req";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function SubmissionPage() {
  const api_link = "http://localhost:2022/api";
  const { id } = useParams(); // this is submission id, not leaderboard id
  const navigate = useNavigate();

  const [ld, setLd] = useState(undefined);
  const [ldName, setLdName] = useState(undefined);
  const [submissions, setSubmissions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(null);

  // Load leaderboard + submissions
  async function loadLeaderboard() {
    const { data } = await axios.post(api_link + "/leaderboard/getLeaderboardById", { id: id }); 
    const ldb = data.leaderboard;
    setLd(ldb);
    setLdName(ldb.name);

    if (ldb.submissions && ldb.submissions.length > 0) {
      setSubmissions(ldb.submissions);

      // find index of requested submission
      const startIndex = ldb.submissions.findIndex((s) => s._id === id);
      setCurrentIndex(startIndex !== -1 ? startIndex : 0);
    }
  }

  useEffect(() => {
    loadLeaderboard();
  }, [id]);

  // Navigate to random submission left or right
  function goToSubmission(direction) {
    if (!submissions.length) return;
    let newIndex;

    if (direction === "left") {
      newIndex = Math.floor(Math.random() * submissions.length);
    } else {
      newIndex = Math.floor(Math.random() * submissions.length);
    }

    setCurrentIndex(newIndex);
    navigate(`/submission/${submissions[newIndex]._id}`);
  }

  if (!ld || currentIndex === null || !submissions[currentIndex]) {
    return (
      <div className="min-h-screen bg-black text-white flex items-center justify-center">
        <p>Loading...</p>
      </div>
    );
  }

  const submission = submissions[currentIndex];

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-zinc-900 to-black text-white">
      <Navbar />

      <div className="max-w-6xl mx-auto px-4 py-10">
        <h1 className="text-4xl font-bold mb-8 text-center">
          Viewing Submission in <span className="text-purple-400">{ldName}</span>
        </h1>

        <div className="relative flex items-center justify-center">
          {/* Left arrow */}
          <button
            onClick={() => goToSubmission("left")}
            className="absolute left-0 p-4 text-purple-400 hover:text-purple-200 text-3xl"
          >
            ←
          </button>

          {/* Submission card */}
          <div className="bg-zinc-950 rounded-2xl p-6 shadow-[0_0_25px_#a855f7aa] max-w-2xl">
            <h2 className="text-2xl font-semibold mb-2 text-purple-400">{submission.name}</h2>
            <p className="text-sm text-gray-300 mb-4">ELO: {Math.round(submission.elo)}</p>

            <div className="space-y-3">
              {ld.required.map((req, i) => {
                const name = req.name;

                const keys = Object.keys(submission.data).filter((b) => {
                  const escapeRegex = (str) =>
                    str.replace(/[-\/\\^$*+?.()|[\]{}]/g, "\\$&");
                  const regex = new RegExp(`^${escapeRegex(name)} \\d+$`);
                  return b === name || regex.test(b);
                });

                const entries = keys.map((x) => submission.data[x]);
                const caption_keys = keys.map((x) => `${x} caption`);
                const captions = caption_keys.map((x) => submission.data[x]);

                return (
                  <Req
                    key={i}
                    entries={entries}
                    reqName={name}
                    i={i}
                    dtype={req.type}
                    captions={captions}
                  />
                );
              })}
            </div>
          </div>

          {/* Right arrow */}
          <button
            onClick={() => goToSubmission("right")}
            className="absolute right-0 p-4 text-purple-400 hover:text-purple-200 text-3xl"
          >
            →
          </button>
        </div>
      </div>
    </div>
  );
}
