import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import AddLd from "../components/AddLd";
import axios from "axios";

export default function Dashboard() {
  const navigate = useNavigate();
  const api_link = "http://localhost:2022/api";

  const [user, setUser] = useState({});
  const [leaderboardData, setLeaderboardData] = useState([]);
  const [showCreateLd, setShowCreateLd] = useState(false);
  const [subs, setSubs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchMatches, setSearchMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  async function updateSearch() {
    if (searchTerm !== "") {
      const { data } = await axios.post(api_link + "/leaderboard/searchForLd", {
        leaderboard: searchTerm,
      });
      setSearchMatches(data.results);
    } else {
      setSearchMatches([]);
    }
  }

  useEffect(() => {
    updateSearch();
  }, [searchTerm]);

  async function updateData(user) {
    setUser(user);
    const leaderboard = [];
    const sub = [];

    for (const id of user.leaderboardIds || []) {
      const { data } = await axios.post(api_link + "/leaderboard/getLeaderboardById", { id });
      data.leaderboard.id = id;
      leaderboard.push(data.leaderboard);
    }

    for (const id of user.submissions || []) {
      const { data } = await axios.post(api_link + "/leaderboard/getSubmissionById", { id });
      sub.push(data.submission);
    }

    setLeaderboardData(leaderboard);
    setSubs(sub);
    setLoading(false);
  }

  useEffect(() => {
    let userObj = localStorage.getItem("user");
    if (!userObj) {
      navigate("/login");
    } else {
      userObj = JSON.parse(userObj);
      updateData(userObj);
    }
  }, []);

  return (
    <div className="px-8 py-8 min-h-screen bg-black text-white">
      <Navbar />
      <AddLd
        show={showCreateLd}
        exit={() => setShowCreateLd(false)}
        ldStateUpdate={(usr) => updateData(usr)}
      />

      {loading ? (
        <div className="flex justify-center items-center min-h-[70vh]">
          <div className="w-12 h-12 border-4 border-white border-t-yellow-400 rounded-full animate-spin"></div>
        </div>
      ) : (
        <>
          {/* Search */}
          <h1 className="font-extrabold text-yellow-400 text-3xl mb-6 drop-shadow-[0_0_15px_rgba(234,179,8,0.8)]">
            Find Leaderboards
          </h1>

          <input
            type="text"
            onChange={(e) => setSearchTerm(e.target.value)}
            value={searchTerm}
            placeholder="Search leaderboards..."
            className="w-full p-3 bg-black border-2 border-yellow-400 rounded-lg mb-8 text-yellow-400 placeholder-yellow-700 outline-none focus:ring-2 focus:ring-yellow-500 shadow-[0_0_12px_rgba(234,179,8,0.8)] transition"
          />

          {searchMatches.length > 0 && (
            <div className="grid gap-6 grid-cols-1 sm:grid-cols-2 md:grid-cols-3 mb-10">
              {searchMatches.map((ld, key) => (
                <a
                  key={key}
                  href={`/leaderboard/${ld._id}`}
                  className="bg-gradient-to-tr from-yellow-700 via-purple-900 to-purple-800 hover:from-yellow-600 hover:via-purple-700 hover:to-purple-700 rounded-2xl shadow-lg p-6 transition-transform duration-300 hover:scale-[1.05] border border-yellow-400"
                >
                  <h2 className="text-xl font-bold text-yellow-300 mb-2 drop-shadow-[0_0_8px_rgba(234,179,8,0.9)]">
                    {ld.name}
                  </h2>
                  <p className="text-yellow-100 text-sm italic line-clamp-3">
                    {ld.description || "No description available."}
                  </p>
                </a>
              ))}
            </div>
          )}

          {/* Your Leaderboards */}
          <div className="flex flex-row items-center w-full mb-6">
            <h1 className="font-extrabold text-yellow-400 text-3xl drop-shadow-[0_0_15px_rgba(234,179,8,0.8)]">
              Your Leaderboards
            </h1>
            <button
              className="ml-auto px-6 py-2 bg-yellow-500 hover:bg-yellow-600 text-black text-2xl rounded-2xl shadow-[0_0_15px_rgba(234,179,8,0.8)] font-extrabold transition"
              onClick={() => setShowCreateLd(true)}
              aria-label="Create leaderboard"
            >
              +
            </button>
          </div>

          <div className="w-full flex flex-row flex-wrap gap-8">
            {leaderboardData.length === 0 ? (
              <p className="italic text-yellow-300">No leaderboards yet. Click + to create one.</p>
            ) : (
              leaderboardData.map((ld, key) => (
                <a
                  href={`/leaderboard/${ld.id}`}
                  key={key}
                  className="bg-gradient-to-br from-purple-900 via-purple-800 to-purple-900 hover:from-purple-700 hover:via-purple-700 hover:to-purple-800 rounded-2xl shadow-[0_0_15px_rgba(170,0,255,0.9)] p-6 w-full md:w-[45%] transition-transform duration-300 hover:scale-[1.03]"
                >
                  <h2 className="text-2xl font-bold text-yellow-400 mb-4 drop-shadow-[0_0_12px_rgba(234,179,8,0.8)]">
                    {ld.name}
                  </h2>
                  {ld.order && ld.order.length > 0 ? (
                    <div className="space-y-2">
                      {ld.order.map((subname, subKey) => {
                        const sub = ld.submissions[subname];
                        return (
                          <div
                            key={subKey}
                            className="flex justify-between items-center bg-purple-900 px-4 py-2 rounded-lg shadow-inner"
                          >
                            <p className="text-yellow-300 font-semibold">{subname}</p>
                            <div className="flex gap-6 text-sm text-yellow-100">
                              <p>
                                Rank:{" "}
                                <span className="font-extrabold">{sub.rank}</span>
                              </p>
                              <p>
                                ELO:{" "}
                                <span className="font-extrabold">{parseInt(sub.elo)}</span>
                              </p>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  ) : (
                    <p className="text-yellow-200 italic">No submissions yet.</p>
                  )}
                </a>
              ))
            )}
          </div>

          {/* Submissions */}
          <h1 className="font-extrabold text-yellow-400 text-3xl mt-12 mb-6 drop-shadow-[0_0_15px_rgba(234,179,8,0.8)]">
            Your Submissions
          </h1>
          {subs.length === 0 ? (
            <p className="italic text-yellow-300 text-center mt-10">
              No submissions yet. Go find a cool leaderboard and submit to it!
            </p>
          ) : (
            <div className="grid md:grid-cols-2 gap-8 mt-6">
              {subs.map((sub, key) => (
                <div
                  key={key}
                  className="bg-gradient-to-tr from-yellow-600 to-purple-900 rounded-3xl p-8 shadow-[0_0_20px_rgba(234,179,8,0.9)] hover:scale-[1.02] transition-transform duration-300"
                >
                  <p className="text-6xl font-extrabold text-center text-yellow-400 mb-6 drop-shadow-[0_0_12px_rgba(234,179,8,0.8)]">
                    #{sub.rank}
                  </p>
                  <div className="text-xl space-y-2 text-center text-yellow-100 font-semibold">
                    <p>{sub.name}</p>
                    <p>ELO: {parseInt(sub.elo)}</p>
                    <a
                      href={`/leaderboard/${sub.leaderboardId}`}
                      className="text-yellow-300 underline hover:text-yellow-100 transition"
                    >
                      Leaderboard: {sub.leaderboard}
                    </a>
                  </div>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}
