import React from "react";
import EmbeddedLink from "../EmbeddedLink.js"

export default function SingleReq({ dtype, k, v }) {
  return (
    <>
      {dtype === "text" && (
        <div className="text-sm">
          <span className="text-gray-400 break-inside-avoid">{k}</span>
          <div className="text-white">
            {/* If needed, use dangerouslySetInnerHTML here */}
            {v}
          </div>
        </div>
      )}

      {dtype === "link" && <EmbeddedLink k={k} v={v} />}

      {dtype === "image" && (
        <div>
          <span className="text-sm text-gray-400">{k}</span>
          <img
            src={decompressToBase64(v)}
            alt=""
            className="h-60 w-auto object-contain rounded"
          />
        </div>
      )}

      {dtype === "audio" && (
        <div>
          <span className="text-sm text-gray-400">{k}</span>
          <audio controls className="w-full">
            <source src={decompressToBase64(v)} type="audio/mpeg" />
          </audio>
        </div>
      )}

      {dtype === "video" && (
        <div>
          <span className="text-sm text-gray-400">{k}</span>
          <video controls className="h-72 w-auto object-contain rounded">
            <source src={decompressToBase64(v)} type="video/mp4" />
          </video>
        </div>
      )}
    </>
  );
}
