import { useState, useEffect, useRef } from "react";
import SingleReq from "./SingleReq";

export default function ReqModal({ entries, reqName, dtype, captions, isOpen, onClose }) {
  const modalRef = useRef(null);
  const [position, setPosition] = useState({ top: 0, left: 0 });
  const [windowSize, setWindowSize] = useState({ width: 0, height: 0 });

  useEffect(() => {
    const updatePosition = () => {
      const w = window.innerWidth;
      const h = window.innerHeight;
      setWindowSize({ width: w, height: h });

      if (modalRef.current) {
        const rect = modalRef.current.getBoundingClientRect();
        setPosition({
          top: Math.max((w - rect.width) / 2, 0),
          left: Math.max((h - rect.height) / 2, 0),
        });
      }
    };

    updatePosition();
    window.addEventListener("resize", updatePosition);
    return () => window.removeEventListener("resize", updatePosition);
  }, [isOpen]);

  if (!isOpen) return null;

  return (
    <div
      style={{ width: windowSize.width, height: windowSize.height, position: "absolute", top: 0, left: 0 }}
      className="bg-black/90 z-50"
      onClick={onClose}
    >
      <div
        ref={modalRef}
        style={{ position: "absolute", top: position.left, left: position.top }}
        className="bg-zinc-900 text-white p-6 shadow-[0_0_20px_#a855f7aa] flex flex-col justify-center"
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-white hover:text-purple-400 text-2xl font-bold"
        >
          Ã—
        </button>

        <h2 className="text-2xl font-semibold text-purple-400 mb-6 text-center">
          {reqName}
        </h2>

        {/* Slides would go here */}
      </div>
    </div>
  );
}
