import { useState, useEffect } from "react";

export default function ReqModal({ entries, reqName, dtype, captions, isOpen, onClose }) {
  const [windowSize, setWindowSize] = useState({
    width: typeof window !== "undefined" ? window.innerWidth : 0,
    height: typeof window !== "undefined" ? window.innerHeight : 0,
  });

  // Update size on window resize
  useEffect(() => {
    const handleResize = () => {
      setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Build slides (same as before)
  const slides = [];
  let currentSlide = [];
  let ind = 0;

  for (const entry of entries) {
    const comp = (
      <SingleReq
        idx={ind}
        dtype={dtype}
        k={captions[ind] || reqName}
        v={entry}
        key={ind}
      />
    );

    if (
      dtype === "image" ||
      dtype === "video" ||
      (dtype === "link" &&
        ((entry.includes("youtube") && entry.includes("embed")) ||
          (entry.match(/\.(jpeg|jpg|gif|png|webp|svg)$/i) || entry.startsWith("data:image")) ||
          entry.match(/\.(mp4|webm|ogg)$/i) ||
          entry.match(/\.(mp3|wav|ogg)$/i)))
    ) {
      if (currentSlide.length > 0) slides.push(currentSlide);
      slides.push([comp]);
      currentSlide = [];
    } else {
      currentSlide.push(comp);
    }

    ind += 1;
  }

  if (currentSlide.length > 0) slides.push(currentSlide);

  const [idx, setIdx] = useState(0);
  const [fade, setFade] = useState(true);

  const prevSlide = () => {
    setFade(false);
    setTimeout(() => {
      setIdx((prev) => (prev - 1 + slides.length) % slides.length);
      setFade(true);
    }, 300);
  };

  const nextSlide = () => {
    setFade(false);
    setTimeout(() => {
      setIdx((prev) => (prev + 1) % slides.length);
      setFade(true);
    }, 300);
  };

  if (!isOpen) return null;

  return (
    <div
      className="absolute top-0 left-0 z-50 bg-black/90 flex items-center justify-center"
      style={{ width: windowSize.width, height: windowSize.height }}
      onClick={onClose}
    >
      <div
        className="relative p-6 flex flex-col justify-center bg-zinc-900 text-white shadow-[0_0_20px_#a855f7aa]"
        style={{ maxWidth: windowSize.width, maxHeight: windowSize.height }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Close button */}
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-white hover:text-purple-400 text-2xl font-bold"
          aria-label="Close modal"
        >
          ×
        </button>

        {/* Title */}
        <h2 className="text-2xl font-semibold text-purple-400 mb-6 text-center">
          {reqName}
        </h2>

        {/* Slides */}
        <ul
          className={`flex flex-col gap-4 list-none transition-opacity duration-300 ease-in-out items-center ${
            fade ? "opacity-100" : "opacity-0"
          }`}
        >
          {slides.length > 0 &&
            slides[idx].map((comp, i) => <li key={i}>{comp}</li>)}
        </ul>

        {/* Navigation */}
        {slides.length > 1 && (
          <div className="flex justify-center gap-4 mt-6">
            <button
              onClick={prevSlide}
              className="hover:bg-purple-700 text-white w-12 h-12 flex items-center justify-center rounded-full text-2xl"
              aria-label="Previous slide"
            >
              ‹
            </button>
            <button
              onClick={nextSlide}
              className="hover:bg-purple-700 text-white w-12 h-12 flex items-center justify-center rounded-full text-2xl"
              aria-label="Next slide"
            >
              ›
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
