import { useState } from "react";
import axios from "axios";

export default function DevpostAdd({ show, exit, user, ldStateUpdate }) {
  const apiuri = "http://localhost:9000/devpost";
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState([]);

  async function returnSearchTerms() {
    try {
      const { data } = await axios.post(apiuri + "/search", {
        query: searchTerm,
      });

      if (!data.error) {
        setResults(data.results || []);
      } else {
        document.getElementById("error").innerHTML = data.error;
      }
    } catch (err) {
        console.log(err)
      document.getElementById("error").innerHTML = "Search failed.";
    }
  }

  async function addFromDevpost() {
    try {
      const data= await axios.post(apiuri + "/create", {
        admin: user.username,
        hackathon_name: searchTerm,
      });

      if (data.error) {
        document.getElementById("error").innerHTML = data.error;
      } else {
        console.log(data)
        localStorage.setItem("user", JSON.stringify(data.user));
        ldStateUpdate(data.user);
        exit();
      }
    } catch (err) {
      document.getElementById("error").innerHTML = "Creation failed.";
    }
  }

  return (
    <div
      className={`${
        show
          ? "scale-100 opacity-100"
          : "scale-90 opacity-0 pointer-events-none"
      } transition-all duration-200 fixed inset-0 flex items-center justify-center bg-black bg-opacity-60 z-50`}
    >
      <div className="bg-zinc-950 p-8 rounded-2xl shadow-[0_0_30px_#a855f7] w-[90%] max-w-2xl h-[90%] overflow-y-auto border border-purple-700">
        {/* Header */}
        <div className="flex items-center mb-6">
          <h1 className="text-2xl font-bold text-purple-400 drop-shadow-[0_0_6px_#a855f7]">
            Import from Devpost
          </h1>
          <button
            onClick={exit}
            className="ml-auto text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded text-lg"
          >
            âœ•
          </button>
        </div>

        {/* Search bar */}
        <div className="flex mb-4">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-1 px-4 py-2 rounded-l-lg bg-black border border-purple-700 text-white placeholder-gray-400 focus:outline-none"
            placeholder="Search hackathons..."
          />
          <button
            onClick={returnSearchTerms}
            className="px-4 py-2 bg-purple-600 hover:bg-purple-700 rounded-r-lg text-white font-semibold"
          >
            Search
          </button>
        </div>

        {/* Results */}
        <div className="mb-4 space-y-2 max-h-48 overflow-y-auto">
          {results.map((item, idx) => (
            <div
              key={idx}
              onClick={() => setSearchTerm(item)}
              className="cursor-pointer px-3 py-2 bg-zinc-800 hover:bg-purple-700 rounded-lg text-white"
            >
              {item}
            </div>
          ))}
        </div>

        {/* Add */}
        <button
          onClick={addFromDevpost}
          className="w-full mt-2 bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-lg shadow-md"
        >
          Add Hackathon
        </button>

        {/* Error */}
        <p id="error" className="text-red-500 italic font-bold mt-4"></p>
      </div>
    </div>
  );
}
